declare(strict_types=1);


use OCP\IConfig;

use OCA\UserOIDC\Db\Provider;
use OCA\UserOIDC\Service\JwtService;
use OCA\UserOIDC\Service\SignatureException;

use PHPUnit\Framework\Assert;
use PHPUnit\Framework\TestCase;

use Jose\Component\Signature\JWS;
use Jose\Component\Core\Util\JsonConverter;

class JwtServiceTest extends TestCase {

	/**
	 * @var ProviderService
	 */
	private $provider;

	/**
	 * @var JwtService
	 */
	private $jwtService;

	public function setUp(): void {
		parent::setUp();
		$this->jwtService = \OC::$server->get(JwtService::class);
	}

	// Refresh RT2:ea5ecf7f-201d-4b3f-902d-35c993686dfc:eb02cd95-b886-418f-ac88-bb5aa49a93ec
	// by curl:
	// curl -X POST -H "Accept: application/json" -H "Content-Type: application/x-www-form-urlencoded" -d "client_id=10TVL0SAM30000004901NEXTMAGENTACLOUD0000&client_secret=FGW2D9B9-BB9C-GMFD-XCE6-A4E79C759CRN&&grant_type=refresh_token&scope=magentacloud&refresh_token=RT2:ea5ecf7f-201d-4b3f-902d-35c993686dfc:eb02cd95-b886-418f-ac88-bb5aa49a93ec" https://accounts.login00.idm.ver.sul.t-online.de/oauth2/tokens
    //
	public function testValidSignature() {
        $this->expectNotToPerformAssertions();
		$testtoken = 'eyJhbGciOiJIUzI1NiJ9..8WG42MmK3ZTUyHl3eF7XTz3l9Z_yk_mTPJxuZ55laoQ';

		$serializerManager = new \Jose\Component\Signature\Serializer\JWSSerializerManager([
				new \Jose\Component\Signature\Serializer\CompactSerializer()
			]);
		$decodedToken = $serializerManager->unserialize($testtoken);
		$this->jwtService->verifySignature($decodedToken, \Base64Url\Base64Url::encode('JQ17C99A-DAF8-4E27-FBW4-GV23B043C993'));	
	}

	public function testInvalidSignature() {
		$this->expectException(SignatureException::class);
		$testtoken = 'eyJhbGciOiJIUzI1NiJ9..8WG42MmK3ZTUyHl3eF7XTz3l8Z_yk_mTPJxuZ55laoQ';

		$serializerManager = new \Jose\Component\Signature\Serializer\JWSSerializerManager([
				new \Jose\Component\Signature\Serializer\CompactSerializer()
			]);
		
		$decodedToken = $serializerManager->unserialize($testtoken);
		$this->jwtService->verifySignature($decodedToken, \Base64Url\Base64Url::encode('JQ17C99A-DAF8-4E27-FBW4-GV23B043C993'));
		//fwrite(STDERR, json_encode($decodedToken->getPayload()));
	}


	public function testDecodeAndSignature() {
        $this->expectNotToPerformAssertions();
		$testtoken = 'eyJhbGciOiJIUzI1NiJ9..8WG42MmK3ZTUyHl3eF7XTz3l9Z_yk_mTPJxuZ55laoQ';

		$serializerManager = new \Jose\Component\Signature\Serializer\JWSSerializerManager([
				new \Jose\Component\Signature\Serializer\CompactSerializer()
			]);
		
		$decodedToken = $this->jwtService->decryptToken($testtoken, \Base64Url\Base64Url::encode('JQ17C99A-DAF8-4E27-FBW4-GV23B043C993'));
		$this->jwtService->verifySignature($decodedToken, \Base64Url\Base64Url::encode('JQ17C99A-DAF8-4E27-FBW4-GV23B043C993'));
	}


}
